import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";
import { webSocketService } from "./services/websocket";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
    // Initialize WebSocket server
    webSocketService.init(server);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { authService } from "./services/auth";
import { authMiddleware, requireRole } from "./middleware/auth";
import { fileUploadService } from "./services/file-upload";
import { geolocationService } from "./services/geolocation";
import { webSocketService } from "./services/websocket";
import multer from "multer";
import { z } from "zod";
import { insertIssueSchema, insertCommentSchema, insertUserSchema } from "@shared/schema";

const upload = multer({ storage: multer.memoryStorage(), limits: { fileSize: 10 * 1024 * 1024 } });

// Validation schemas
const loginSchema = z.object({
  email: z.string().email(),
  password: z.string().min(1)
});

const registerSchema = insertUserSchema.extend({
  password: z.string().min(6)
});

const createIssueSchema = insertIssueSchema.extend({
  location: z.object({
    lat: z.number(),
    lng: z.number()
  }).optional()
});

export async function registerRoutes(app: Express): Promise<Server> {
  // Health check
  app.get("/api/health", async (req, res) => {
    res.json({ status: "ok", timestamp: new Date().toISOString() });
  });

  // Auth routes
  app.post("/api/auth/register", async (req, res) => {
    try {
      const validatedData = registerSchema.parse(req.body);
      const hashedPassword = await authService.hashPassword(validatedData.password);

      const user = await storage.createUser({
        ...validatedData,
        password: hashedPassword
      });

      const tokens = await authService.generateTokens(user);

      res.json({
        user: { ...user, password: undefined },
        ...tokens
      });
    } catch (error) {
      res.status(400).json({ 
        message: error instanceof Error ? error.message : "Registration failed" 
      });
    }
  });

  app.post("/api/auth/login", async (req, res) => {
    try {
      const { email, password } = loginSchema.parse(req.body);
      const user = await storage.getUserByEmail(email);

      if (!user || !await authService.verifyPassword(password, user.password)) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      if (!user.isActive) {
        return res.status(403).json({ message: "Account is deactivated" });
      }

      const tokens = await authService.generateTokens(user);

      res.json({
        user: { ...user, password: undefined },
        ...tokens
      });
    } catch (error) {
      res.status(400).json({ 
        message: error instanceof Error ? error.message : "Login failed" 
      });
    }
  });

  app.post("/api/auth/refresh", async (req, res) => {
    try {
      const { refreshToken } = req.body;
      if (!refreshToken) {
        return res.status(401).json({ message: "Refresh token required" });
      }

      const tokens = await authService.refreshTokens(refreshToken);
      res.json(tokens);
    } catch (error) {
      res.status(401).json({ message: "Invalid refresh token" });
    }
  });

  app.get("/api/auth/me", authMiddleware, async (req, res) => {
    try {
      const user = await storage.getUser(req.user!.id);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      res.json({ ...user, password: undefined });
    } catch (error) {
      res.status(500).json({ message: "Failed to get user data" });
    }
  });

  // File upload
  app.post("/api/media/upload", authMiddleware, upload.array('files', 5), async (req, res) => {
    try {
      if (!req.files || !Array.isArray(req.files)) {
        return res.status(400).json({ message: "No files uploaded" });
      }

      const uploadPromises = req.files.map(file => fileUploadService.uploadFile(file));
      const uploadedUrls = await Promise.all(uploadPromises);

      res.json({ urls: uploadedUrls });
    } catch (error) {
      res.status(500).json({ 
        message: error instanceof Error ? error.message : "File upload failed" 
      });
    }
  });

  // Catalog endpoints
  app.get("/api/catalog/categories", async (req, res) => {
    try {
      const categories = await storage.getCategories();
      res.json(categories);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch categories" });
    }
  });

  app.get("/api/catalog/wards", async (req, res) => {
    try {
      const wards = await storage.getWards();
      res.json(wards);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch wards" });
    }
  });

  app.get("/api/catalog/departments", async (req, res) => {
    try {
      const departments = await storage.getDepartments();
      res.json(departments);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch departments" });
    }
  });

  // Issue routes - Citizen
  app.post("/api/issues", authMiddleware, async (req, res) => {
    try {
      const validatedData = createIssueSchema.parse(req.body);
      const userId = req.user!.id;

      let latitude = null;
      let longitude = null;
      if (validatedData.location) {
        latitude = validatedData.location.lat.toString();
        longitude = validatedData.location.lng.toString();

        // Get address from coordinates
        try {
          const address = await geolocationService.reverseGeocode(
            validatedData.location.lat,
            validatedData.location.lng
          );
          validatedData.address = address;
        } catch (error) {
          console.warn("Geocoding failed:", error);
        }
      }

      // Auto-assign department based on category
      const category = await storage.getCategories();
      const selectedCategory = category.find(c => c.id === validatedData.categoryId);

      const issue = await storage.createIssue({
        ...validatedData,
        latitude,
        longitude,
        reporterId: userId,
        departmentId: selectedCategory?.departmentId || undefined,
        status: 'SUBMITTED'
      });

      // Create issue event
      await storage.createIssueEvent({
        issueId: issue.id,
        actorId: userId,
        type: 'STATUS_CHANGE',
        payload: { oldStatus: 'DRAFT', newStatus: 'SUBMITTED' }
      });

      // Send real-time notification
      webSocketService.notifyNewIssue(issue);

      res.status(201).json(issue);
    } catch (error) {
      res.status(400).json({ 
        message: error instanceof Error ? error.message : "Failed to create issue" 
      });
    }
  });

  app.get("/api/issues/my", authMiddleware, async (req, res) => {
    try {
      const userId = req.user!.id;
      const page = parseInt(req.query.page as string) || 1;
      const limit = parseInt(req.query.limit as string) || 10;
      const status = req.query.status as string;

      const result = await storage.getIssues({
        reporterId: userId,
        status,
        page,
        limit
      });

      res.json(result);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch issues" });
    }
  });

  app.get("/api/issues/:ticketNo", authMiddleware, async (req, res) => {
    try {
      const { ticketNo } = req.params;
      const issue = await storage.getIssueByTicketNo(ticketNo);

      if (!issue) {
        return res.status(404).json({ message: "Issue not found" });
      }

      // Check if user can access this issue
      const userId = req.user!.id;
      const userRole = req.user!.role;

      if (userRole === 'CITIZEN' && issue.reporterId !== userId) {
        return res.status(403).json({ message: "Access denied" });
      }

      res.json(issue);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch issue" });
    }
  });

  app.post("/api/issues/:id/comments", authMiddleware, async (req, res) => {
    try {
      const { id: issueId } = req.params;
      const userId = req.user!.id;
      const commentData = insertCommentSchema.parse({
        ...req.body,
        issueId,
        authorId: userId
      });

      const comment = await storage.createComment(commentData);

      // Create issue event
      await storage.createIssueEvent({
        issueId,
        actorId: userId,
        type: 'COMMENT',
        payload: { commentId: comment.id }
      });

      res.status(201).json(comment);
    } catch (error) {
      res.status(400).json({ 
        message: error instanceof Error ? error.message : "Failed to create comment" 
      });
    }
  });

  // Admin routes
  app.get("/api/admin/issues", authMiddleware, requireRole(['OFFICER', 'SUPERVISOR', 'ADMIN']), async (req, res) => {
    try {
      const {
        page = '1',
        limit = '10',
        status,
        categoryId,
        wardId,
        departmentId,
        search
      } = req.query;

      const result = await storage.getIssues({
        status: status as string,
        categoryId: categoryId as string,
        wardId: wardId as string,
        departmentId: departmentId as string,
        search: search as string,
        page: parseInt(page as string),
        limit: parseInt(limit as string)
      });

      res.json(result);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch issues" });
    }
  });

  app.patch("/api/admin/issues/:id/status", authMiddleware, requireRole(['OFFICER', 'SUPERVISOR', 'ADMIN']), async (req, res) => {
    try {
      const { id } = req.params;
      const { status, rejectedReason } = req.body;
      const userId = req.user!.id;

      const currentIssue = await storage.getIssue(id);
      if (!currentIssue) {
        return res.status(404).json({ message: "Issue not found" });
      }

      const updateData: any = { status };
      if (status === 'RESOLVED') {
        updateData.resolvedAt = new Date();
      }
      if (status === 'REJECTED' && rejectedReason) {
        updateData.rejectedReason = rejectedReason;
      }

      const updatedIssue = await storage.updateIssue(id, updateData);

      // Create issue event
      await storage.createIssueEvent({
        issueId: id,
        actorId: userId,
        type: 'STATUS_CHANGE',
        payload: { oldStatus: currentIssue.status, newStatus: status, rejectedReason }
      });

      res.json(updatedIssue);
    } catch (error) {
      res.status(400).json({ 
        message: error instanceof Error ? error.message : "Failed to update issue status" 
      });
    }
  });

  app.patch("/api/admin/issues/:id/assign", authMiddleware, requireRole(['SUPERVISOR', 'ADMIN']), async (req, res) => {
    try {
      const { id } = req.params;
      const { assigneeId } = req.body;
      const userId = req.user!.id;

      const updatedIssue = await storage.updateIssue(id, { 
        assigneeId,
        status: 'ASSIGNED'
      });

      // Create issue event
      await storage.createIssueEvent({
        issueId: id,
        actorId: userId,
        type: 'ASSIGN',
        payload: { assigneeId }
      });

      res.json(updatedIssue);
    } catch (error) {
      res.status(400).json({ 
        message: error instanceof Error ? error.message : "Failed to assign issue" 
      });
    }
  });

  app.get("/api/admin/analytics/overview", authMiddleware, requireRole(['OFFICER', 'SUPERVISOR', 'ADMIN']), async (req, res) => {
    try {
      const stats = await storage.getIssueStats();
      res.json(stats);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch analytics" });
    }
  });

  app.get("/api/admin/users", authMiddleware, requireRole(['SUPERVISOR', 'ADMIN']), async (req, res) => {
    try {
      const users = await storage.getUsersWithStats();
      res.json(users.map(user => ({ ...user, password: undefined })));
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch users" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  users, departments, wards, categories, issues, issueEvents, 
  comments, notifications, slaBreaches,
  type User, type InsertUser, type Department, type InsertDepartment,
  type Ward, type InsertWard, type Category, type InsertCategory,
  type Issue, type InsertIssue, type IssueEvent, type Comment, 
  type InsertComment, type Notification, type InsertNotification,
  type IssueWithRelations, type UserWithStats
} from "@shared/schema";
import { db } from "./db";
import { eq, and, desc, asc, count, sql, ilike, inArray } from "drizzle-orm";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: string, user: Partial<InsertUser>): Promise<User>;
  getUsersWithStats(): Promise<UserWithStats[]>;

  // Department operations
  getDepartments(): Promise<Department[]>;
  createDepartment(department: InsertDepartment): Promise<Department>;

  // Ward operations
  getWards(): Promise<Ward[]>;
  createWard(ward: InsertWard): Promise<Ward>;

  // Category operations
  getCategories(): Promise<Category[]>;
  getCategoriesByDepartment(departmentId: string): Promise<Category[]>;
  createCategory(category: InsertCategory): Promise<Category>;

  // Issue operations
  getIssues(params: {
    status?: string;
    categoryId?: string;
    wardId?: string;
    departmentId?: string;
    reporterId?: string;
    assigneeId?: string;
    page?: number;
    limit?: number;
    search?: string;
  }): Promise<{ issues: IssueWithRelations[], total: number }>;
  getIssue(id: string): Promise<IssueWithRelations | undefined>;
  getIssueByTicketNo(ticketNo: string): Promise<IssueWithRelations | undefined>;
  createIssue(issue: InsertIssue): Promise<Issue>;
  updateIssue(id: string, issue: Partial<InsertIssue>): Promise<Issue>;
  getIssueStats(): Promise<{
    total: number;
    byStatus: Record<string, number>;
    byPriority: Record<string, number>;
    byCategory: Record<string, number>;
    byWard: Record<string, number>;
  }>;

  // Comment operations
  getCommentsByIssue(issueId: string): Promise<(Comment & { author: User })[]>;
  createComment(comment: InsertComment): Promise<Comment>;

  // Event operations
  createIssueEvent(event: Omit<IssueEvent, 'id' | 'createdAt'>): Promise<IssueEvent>;

  // Notification operations
  getNotificationsByUser(userId: string): Promise<Notification[]>;
  createNotification(notification: InsertNotification): Promise<Notification>;
  markNotificationAsRead(id: string): Promise<void>;
}

export class DatabaseStorage implements IStorage {
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db.insert(users).values(insertUser).returning();
    return user;
  }

  async updateUser(id: string, updateData: Partial<InsertUser>): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ ...updateData, updatedAt: new Date() })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  async getUsersWithStats(): Promise<UserWithStats[]> {
    const usersWithStats = await db
      .select({
        id: users.id,
        email: users.email,
        name: users.name,
        phone: users.phone,
        role: users.role,
        wardId: users.wardId,
        isActive: users.isActive,
        createdAt: users.createdAt,
        updatedAt: users.updatedAt,
        fcmToken: users.fcmToken,
        locale: users.locale,
        password: users.password,
        issueCount: sql<number>`count(${issues.id})`.mapWith(Number),
        resolvedCount: sql<number>`count(case when ${issues.status} = 'RESOLVED' then 1 end)`.mapWith(Number)
      })
      .from(users)
      .leftJoin(issues, eq(issues.assigneeId, users.id))
      .where(inArray(users.role, ['OFFICER', 'SUPERVISOR']))
      .groupBy(users.id)
      .orderBy(asc(users.name));

    return usersWithStats;
  }

  async getDepartments(): Promise<Department[]> {
    return await db.select().from(departments).orderBy(asc(departments.name));
  }

  async createDepartment(department: InsertDepartment): Promise<Department> {
    const [newDept] = await db.insert(departments).values(department).returning();
    return newDept;
  }

  async getWards(): Promise<Ward[]> {
    return await db.select().from(wards).orderBy(asc(wards.name));
  }

  async createWard(ward: InsertWard): Promise<Ward> {
    const [newWard] = await db.insert(wards).values(ward).returning();
    return newWard;
  }

  async getCategories(): Promise<Category[]> {
    return await db
      .select()
      .from(categories)
      .orderBy(asc(categories.name));
  }

  async getCategoriesByDepartment(departmentId: string): Promise<Category[]> {
    return await db
      .select()
      .from(categories)
      .where(eq(categories.departmentId, departmentId))
      .orderBy(asc(categories.name));
  }

  async createCategory(category: InsertCategory): Promise<Category> {
    const [newCategory] = await db.insert(categories).values(category).returning();
    return newCategory;
  }

  async getIssues(params: {
    status?: string;
    categoryId?: string;
    wardId?: string;
    departmentId?: string;
    reporterId?: string;
    assigneeId?: string;
    page?: number;
    limit?: number;
    search?: string;
  }): Promise<{ issues: IssueWithRelations[], total: number }> {
    const { page = 1, limit = 10, search, ...filters } = params;
    const offset = (page - 1) * limit;

    let whereConditions = [];

    if (filters.status) {
      whereConditions.push(eq(issues.status, filters.status as any));
    }
    if (filters.categoryId) {
      whereConditions.push(eq(issues.categoryId, filters.categoryId));
    }
    if (filters.wardId) {
      whereConditions.push(eq(issues.wardId, filters.wardId));
    }
    if (filters.departmentId) {
      whereConditions.push(eq(issues.departmentId, filters.departmentId));
    }
    if (filters.reporterId) {
      whereConditions.push(eq(issues.reporterId, filters.reporterId));
    }
    if (filters.assigneeId) {
      whereConditions.push(eq(issues.assigneeId, filters.assigneeId));
    }
    if (search) {
      whereConditions.push(ilike(issues.title, `%${search}%`));
    }

    const whereClause = whereConditions.length > 0 ? and(...whereConditions) : undefined;

    const [issuesResult, totalResult] = await Promise.all([
      db
        .select({
          id: issues.id,
          ticketNo: issues.ticketNo,
          title: issues.title,
          description: issues.description,
          status: issues.status,
          priority: issues.priority,
          priorityScore: issues.priorityScore,
          address: issues.address,
          imageUrls: issues.imageUrls,
          videoUrls: issues.videoUrls,
          createdAt: issues.createdAt,
          updatedAt: issues.updatedAt,
          resolvedAt: issues.resolvedAt,
          rejectedReason: issues.rejectedReason,
          categoryId: issues.categoryId,
          wardId: issues.wardId,
          departmentId: issues.departmentId,
          reporterId: issues.reporterId,
          assigneeId: issues.assigneeId,
          duplicateOfIssueId: issues.duplicateOfIssueId,
          latitude: issues.latitude,
          longitude: issues.longitude,
          category: {
            id: categories.id,
            name: categories.name,
            code: categories.code,
            icon: categories.icon,
            slaHours: categories.slaHours,
            priorityWeight: categories.priorityWeight,
            departmentId: categories.departmentId,
            createdAt: categories.createdAt
          },
          ward: {
            id: wards.id,
            name: wards.name,
            code: wards.code,
            geojson: wards.geojson,
            createdAt: wards.createdAt
          },
          department: {
            id: departments.id,
            name: departments.name,
            code: departments.code,
            email: departments.email,
            phone: departments.phone,
            createdAt: departments.createdAt
          },
          reporter: {
            id: users.id,
            name: users.name,
            email: users.email,
            phone: users.phone,
            role: users.role,
            wardId: users.wardId,
            fcmToken: users.fcmToken,
            locale: users.locale,
            isActive: users.isActive,
            createdAt: users.createdAt,
            updatedAt: users.updatedAt,
            password: users.password
          }
        })
        .from(issues)
        .leftJoin(categories, eq(issues.categoryId, categories.id))
        .leftJoin(wards, eq(issues.wardId, wards.id))
        .leftJoin(departments, eq(issues.departmentId, departments.id))
        .leftJoin(users, eq(issues.reporterId, users.id))
        .where(whereClause)
        .orderBy(desc(issues.createdAt))
        .limit(limit)
        .offset(offset),

      db
        .select({ count: count() })
        .from(issues)
        .where(whereClause)
    ]);

    const transformedIssues: IssueWithRelations[] = issuesResult.map(row => ({
      id: row.id,
      ticketNo: row.ticketNo,
      title: row.title,
      description: row.description,
      status: row.status,
      priority: row.priority,
      priorityScore: row.priorityScore,
      address: row.address,
      imageUrls: row.imageUrls,
      videoUrls: row.videoUrls,
      createdAt: row.createdAt,
      updatedAt: row.updatedAt,
      resolvedAt: row.resolvedAt,
      rejectedReason: row.rejectedReason,
      categoryId: row.categoryId,
      wardId: row.wardId,
      departmentId: row.departmentId,
      reporterId: row.reporterId,
      assigneeId: row.assigneeId,
      duplicateOfIssueId: row.duplicateOfIssueId,
      latitude: row.latitude,
      longitude: row.longitude,
      category: row.category?.id ? row.category : undefined,
      ward: row.ward?.id ? row.ward : undefined,
      department: row.department?.id ? row.department : undefined,
      reporter: row.reporter?.id ? row.reporter : undefined
    }));

    return {
      issues: transformedIssues,
      total: totalResult[0].count
    };
  }

  async getIssue(id: string): Promise<IssueWithRelations | undefined> {
    const result = await this.getIssues({ page: 1, limit: 1 });
    const [issue] = await db
      .select()
      .from(issues)
      .where(eq(issues.id, id));

    if (!issue) return undefined;

    const [category, ward, department, reporter, assignee] = await Promise.all([
      issue.categoryId ? db.select().from(categories).where(eq(categories.id, issue.categoryId)).then(r => r[0]) : null,
      issue.wardId ? db.select().from(wards).where(eq(wards.id, issue.wardId)).then(r => r[0]) : null,
      issue.departmentId ? db.select().from(departments).where(eq(departments.id, issue.departmentId)).then(r => r[0]) : null,
      db.select().from(users).where(eq(users.id, issue.reporterId)).then(r => r[0]),
      issue.assigneeId ? db.select().from(users).where(eq(users.id, issue.assigneeId)).then(r => r[0]) : null,
    ]);

    const commentsWithAuthors = await this.getCommentsByIssue(id);

    return {
      ...issue,
      category: category || undefined,
      ward: ward || undefined,
      department: department || undefined,
      reporter: reporter || undefined,
      assignee: assignee || undefined,
      comments: commentsWithAuthors
    };
  }

  async getIssueByTicketNo(ticketNo: string): Promise<IssueWithRelations | undefined> {
    const [issue] = await db
      .select()
      .from(issues)
      .where(eq(issues.ticketNo, ticketNo));

    if (!issue) return undefined;
    return this.getIssue(issue.id);
  }

  async createIssue(issue: InsertIssue): Promise<Issue> {
    // Generate ticket number
    const ticketNo = `CR-${new Date().getFullYear()}-${String(Date.now()).slice(-6)}`;

    const [newIssue] = await db
      .insert(issues)
      .values({ ...issue, ticketNo })
      .returning();

    return newIssue;
  }

  async updateIssue(id: string, updateData: Partial<InsertIssue>): Promise<Issue> {
    const [updatedIssue] = await db
      .update(issues)
      .set({ ...updateData, updatedAt: new Date() })
      .where(eq(issues.id, id))
      .returning();

    return updatedIssue;
  }

  async getIssueStats(): Promise<{
    total: number;
    byStatus: Record<string, number>;
    byPriority: Record<string, number>;
    byCategory: Record<string, number>;
    byWard: Record<string, number>;
  }> {
    const [totalCount, statusStats, priorityStats, categoryStats, wardStats] = await Promise.all([
      db.select({ count: count() }).from(issues),
      db
        .select({ status: issues.status, count: count() })
        .from(issues)
        .groupBy(issues.status),
      db
        .select({ priority: issues.priority, count: count() })
        .from(issues)
        .groupBy(issues.priority),
      db
        .select({ name: categories.name, count: count() })
        .from(issues)
        .leftJoin(categories, eq(issues.categoryId, categories.id))
        .groupBy(categories.name),
      db
        .select({ name: wards.name, count: count() })
        .from(issues)
        .leftJoin(wards, eq(issues.wardId, wards.id))
        .groupBy(wards.name)
    ]);

    return {
      total: totalCount[0].count,
      byStatus: Object.fromEntries(statusStats.map(s => [s.status, s.count])),
      byPriority: Object.fromEntries(priorityStats.map(p => [p.priority || 'UNKNOWN', p.count])),
      byCategory: Object.fromEntries(categoryStats.map(c => [c.name || 'UNKNOWN', c.count])),
      byWard: Object.fromEntries(wardStats.map(w => [w.name || 'UNKNOWN', w.count]))
    };
  }

  async getCommentsByIssue(issueId: string): Promise<(Comment & { author: User })[]> {
    const commentsWithAuthors = await db
      .select({
        id: comments.id,
        issueId: comments.issueId,
        authorId: comments.authorId,
        body: comments.body,
        attachments: comments.attachments,
        createdAt: comments.createdAt,
        author: {
          id: users.id,
          name: users.name,
          email: users.email,
          phone: users.phone,
          role: users.role,
          wardId: users.wardId,
          fcmToken: users.fcmToken,
          locale: users.locale,
          isActive: users.isActive,
          createdAt: users.createdAt,
          updatedAt: users.updatedAt,
          password: users.password
        }
      })
      .from(comments)
      .innerJoin(users, eq(comments.authorId, users.id))
      .where(eq(comments.issueId, issueId))
      .orderBy(asc(comments.createdAt));

    return commentsWithAuthors.map(row => ({
      id: row.id,
      issueId: row.issueId,
      authorId: row.authorId,
      body: row.body,
      attachments: row.attachments,
      createdAt: row.createdAt,
      author: row.author
    }));
  }

  async createComment(comment: InsertComment): Promise<Comment> {
    const [newComment] = await db.insert(comments).values(comment).returning();
    return newComment;
  }

  async createIssueEvent(event: Omit<IssueEvent, 'id' | 'createdAt'>): Promise<IssueEvent> {
    const [newEvent] = await db.insert(issueEvents).values({
      ...event,
      createdAt: new Date()
    }).returning();
    return newEvent;
  }

  async getNotificationsByUser(userId: string): Promise<Notification[]> {
    return await db
      .select()
      .from(notifications)
      .where(eq(notifications.recipientId, userId))
      .orderBy(desc(notifications.createdAt));
  }

  async createNotification(notification: InsertNotification): Promise<Notification> {
    const [newNotification] = await db.insert(notifications).values(notification).returning();
    return newNotification;
  }

  async markNotificationAsRead(id: string): Promise<void> {
    await db
      .update(notifications)
      .set({ isRead: true })
      .where(eq(notifications.id, id));
  }
}

export const storage = new DatabaseStorage();

