server/middleware/auth.ts.content:
import { Request, Response, NextFunction } from "express";
import { authService } from "../services/auth";

declare global {
  namespace Express {
    interface Request {
      user?: {
        id: string;
        email: string;
        role: string;
        wardId?: string;
      };
    }
  }
}

export const authMiddleware = async (req: Request, res: Response, next: NextFunction) => {
  try {
    const authHeader = req.headers.authorization;

    if (!authHeader || !authHeader.startsWith('Bearer ')) {
      return res.status(401).json({ message: "Authorization token required" });
    }

    const token = authHeader.substring(7);
    const user = await authService.verifyAccessToken(token);

    req.user = user;
    next();
  } catch (error) {
    res.status(401).json({ message: "Invalid or expired token" });
  }
};

export const requireRole = (allowedRoles: string[]) => {
  return (req: Request, res: Response, next: NextFunction) => {
    if (!req.user) {
      return res.status(401).json({ message: "Authentication required" });
    }

    if (!allowedRoles.includes(req.user.role)) {
      return res.status(403).json({ 
        message: "Insufficient permissions",
        required: allowedRoles,
        current: req.user.role
      });
    }

    next();
  };
};

server/services/auth.ts.content:
import bcrypt from "bcryptjs";
import jwt from "jsonwebtoken";
import { User } from "@shared/schema";

const JWT_SECRET = process.env.JWT_SECRET || "civic-connect-jwt-secret";
const JWT_REFRESH_SECRET = process.env.JWT_REFRESH_SECRET || "civic-connect-refresh-secret";
const JWT_EXPIRES_IN = process.env.JWT_EXPIRES_IN || "15m";
const JWT_REFRESH_EXPIRES_IN = process.env.JWT_REFRESH_EXPIRES_IN || "7d";

export class AuthService {
  async hashPassword(password: string): Promise<string> {
    return bcrypt.hash(password, 12);
  }

  async verifyPassword(password: string, hash: string): Promise<boolean> {
    return bcrypt.compare(password, hash);
  }

  async generateTokens(user: User): Promise<{
    accessToken: string;
    refreshToken: string;
    expiresIn: number;
  }> {
    const payload = {
      id: user.id,
      email: user.email,
      role: user.role,
      wardId: user.wardId
    };

    const accessToken = jwt.sign(payload, JWT_SECRET, {
      expiresIn: JWT_EXPIRES_IN,
      issuer: "civic-connect",
      audience: "civic-connect-users"
    } as jwt.SignOptions);

    const refreshToken = jwt.sign(
      { id: user.id, tokenType: "refresh" },
      JWT_REFRESH_SECRET,
      {
        expiresIn: JWT_REFRESH_EXPIRES_IN,
        issuer: "civic-connect",
        audience: "civic-connect-users"
      } as jwt.SignOptions
    );

    // Calculate expiration time in seconds
    const expiresIn = this.parseTimeToSeconds(JWT_EXPIRES_IN);

    return { accessToken, refreshToken, expiresIn };
  }

  async verifyAccessToken(token: string): Promise<{
    id: string;
    email: string;
    role: string;
    wardId?: string;
  }> {
    try {
      const decoded = jwt.verify(token, JWT_SECRET, {
        issuer: "civic-connect",
        audience: "civic-connect-users"
      }) as any;

      return {
        id: decoded.id,
        email: decoded.email,
        role: decoded.role,
        wardId: decoded.wardId
      };
    } catch (error) {
      throw new Error("Invalid access token");
    }
  }

  async refreshTokens(refreshToken: string): Promise<{
    accessToken: string;
    refreshToken: string;
    expiresIn: number;
  }> {
    try {
      const decoded = jwt.verify(refreshToken, JWT_REFRESH_SECRET, {
        issuer: "civic-connect",
        audience: "civic-connect-users"
      }) as any;

      if (decoded.tokenType !== "refresh") {
        throw new Error("Invalid token type");
      }

      // In a production app, you'd fetch the user from database
      // and check if the refresh token is still valid
      const { storage } = await import("../storage");
      const user = await storage.getUser(decoded.id);

      if (!user || !user.isActive) {
        throw new Error("User not found or inactive");
      }

      return this.generateTokens(user);
    } catch (error) {
      throw new Error("Invalid refresh token");
    }
  }

  private parseTimeToSeconds(timeStr: string): number {
    const unit = timeStr.slice(-1);
    const value = parseInt(timeStr.slice(0, -1));

    switch (unit) {
      case 's': return value;
      case 'm': return value * 60;
      case 'h': return value * 60 * 60;
      case 'd': return value * 24 * 60 * 60;
      default: return 900; // default 15 minutes
    }
  }
}

export const authService = new AuthService();

server/services/file-upload.ts.content:
import { randomUUID } from "crypto";
import { writeFile, mkdir } from "fs/promises";
import { join } from "path";
import { existsSync } from "fs";

export class FileUploadService {
  private uploadDir = process.env.UPLOAD_DIR || "uploads";

  constructor() {
    this.ensureUploadDir();
  }

  private async ensureUploadDir() {
    if (!existsSync(this.uploadDir)) {
      await mkdir(this.uploadDir, { recursive: true });
    }
  }

  async uploadFile(file: Express.Multer.File): Promise<string> {
    const fileExtension = file.originalname.split('.').pop() || 'bin';
    const fileName = `${randomUUID()}.${fileExtension}`;
    const filePath = join(this.uploadDir, fileName);

    await writeFile(filePath, file.buffer);

    // Return the URL path for accessing the file
    // In production, this would be a CDN URL or S3 URL
    return `/uploads/${fileName}`;
  }

  async uploadMultiple(files: Express.Multer.File[]): Promise<string[]> {
    const uploadPromises = files.map(file => this.uploadFile(file));
    return Promise.all(uploadPromises);
  }

  validateFile(file: Express.Multer.File): boolean {
    const allowedTypes = ['image/jpeg', 'image/png', 'image/gif', 'image/webp'];
    const maxSize = 10 * 1024 * 1024; // 10MB

    if (!allowedTypes.includes(file.mimetype)) {
      throw new Error(`File type ${file.mimetype} is not allowed`);
    }

    if (file.size > maxSize) {
      throw new Error('File size exceeds 10MB limit');
    }

    return true;
  }
}

export const fileUploadService = new FileUploadService();

server/services/geolocation.ts.content:
export class GeolocationService {
  private mapboxToken = process.env.MAPBOX_ACCESS_TOKEN;
  private googleMapsApiKey = process.env.GOOGLE_MAPS_API_KEY;

  async reverseGeocode(lat: number, lng: number): Promise<string> {
    try {
      // Try Mapbox first if token is available
      if (this.mapboxToken) {
        return await this.reverseGeocodeMapbox(lat, lng);
      }

      // Fallback to Google Maps if API key is available
      if (this.googleMapsApiKey) {
        return await this.reverseGeocodeGoogle(lat, lng);
      }

      // Return formatted coordinates if no geocoding service is available
      return `${lat.toFixed(6)}, ${lng.toFixed(6)}`;
    } catch (error) {
      console.error("Geocoding error:", error);
      return `${lat.toFixed(6)}, ${lng.toFixed(6)}`;
    }
  }

  private async reverseGeocodeMapbox(lat: number, lng: number): Promise<string> {
    const url = `https://api.mapbox.com/geocoding/v5/mapbox.places/${lng},${lat}.json?access_token=${this.mapboxToken}`;

    const response = await fetch(url);
    if (!response.ok) {
      throw new Error(`Mapbox geocoding failed: ${response.statusText}`);
    }

    const data = await response.json();

    if (data.features && data.features.length > 0) {
      return data.features[0].place_name;
    }

    return `${lat.toFixed(6)}, ${lng.toFixed(6)}`;
  }

  private async reverseGeocodeGoogle(lat: number, lng: number): Promise<string> {
    const url = `https://maps.googleapis.com/maps/api/geocode/json?latlng=${lat},${lng}&key=${this.googleMapsApiKey}`;

    const response = await fetch(url);
    if (!response.ok) {
      throw new Error(`Google geocoding failed: ${response.statusText}`);
    }

    const data = await response.json();

    if (data.results && data.results.length > 0) {
      return data.results[0].formatted_address;
    }

    return `${lat.toFixed(6)}, ${lng.toFixed(6)}`;
  }

  async detectWard(lat: number, lng: number): Promise<string | null> {
    // This would typically query PostGIS to find which ward polygon contains the point
    // For now, we'll return a mock implementation
    try {
      const { storage } = await import("../storage");
      const wards = await storage.getWards();

      // In a real implementation, this would use PostGIS ST_Contains function
      // to find the ward polygon that contains the point
      // For now, return the first ward as a placeholder
      return wards[0]?.id || null;
    } catch (error) {
      console.error("Ward detection error:", error);
      return null;
    }
  }
}

export const geolocationService = new GeolocationService();

client/src/hooks/use-auth.ts.content:
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { authManager } from "../lib/auth";
import { apiClient } from "../lib/api";
import { User } from "../types";
import { useToast } from "./use-toast";

export function useAuth() {
  const queryClient = useQueryClient();
  const { toast } = useToast();

  const { data: user, isLoading, error } = useQuery({
    queryKey: ['/api/auth/me'],
    queryFn: () => apiClient.getCurrentUser(),
    enabled: authManager.isAuthenticated(),
    retry: false,
  });

  const loginMutation = useMutation({
    mutationFn: ({ email, password }: { email: string; password: string }) =>
      authManager.login(email, password),
    onSuccess: (data) => {
      queryClient.setQueryData(['/api/auth/me'], data.user);
      toast({
        title: "Login successful",
        description: "Welcome back!",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Login failed",
        description: error.message || "Please check your credentials and try again.",
        variant: "destructive",
      });
    },
  });

  const registerMutation = useMutation({
    mutationFn: (userData: {
      name: string;
      email: string;
      phone?: string;
      password: string;
      role?: string;
    }) => authManager.register(userData),
    onSuccess: (data) => {
      queryClient.setQueryData(['/api/auth/me'], data.user);
      toast({
        title: "Registration successful",
        description: "Welcome to CivicConnect!",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Registration failed",
        description: error.message || "Please try again.",
        variant: "destructive",
      });
    },
  });

  const logout = () => {
    authManager.logout();
    queryClient.clear();
  };

  return {
    user: user as User | undefined,
    isLoading: isLoading && authManager.isAuthenticated(),
    isAuthenticated: authManager.isAuthenticated(),
    login: loginMutation.mutateAsync,
    register: registerMutation.mutateAsync,
    logout,
    isLoggingIn: loginMutation.isPending,
    isRegistering: registerMutation.isPending,
  };
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

