  const [isGettingLocation, setIsGettingLocation] = useState(false);

  const { data: categories = [] } = useQuery({
    queryKey: ['/api/catalog/categories'],
    queryFn: () => apiClient.getCategories(),
  });

  const { data: wards = [] } = useQuery({
    queryKey: ['/api/catalog/wards'],
    queryFn: () => apiClient.getWards(),
  });

  const uploadMutation = useMutation({
    mutationFn: (files: File[]) => apiClient.uploadFiles(files),
  });

  const createIssueMutation = useMutation({
    mutationFn: (issueData: any) => apiClient.createIssue(issueData),
    onSuccess: () => {
      toast({
        title: "Issue reported successfully!",
        description: "Thank you for reporting this issue. We'll keep you updated on its progress.",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/issues/my'] });
      setLocation("/track");
    },
    onError: (error: any) => {
      toast({
        title: "Failed to report issue",
        description: error.message || "Please try again later.",
        variant: "destructive",
      });
    },
  });

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files) {
      const files = Array.from(e.target.files);
      if (files.length + selectedFiles.length > 5) {
        toast({
          title: "Too many files",
          description: "You can only upload up to 5 photos.",
          variant: "destructive",
        });
        return;
      }
      setSelectedFiles([...selectedFiles, ...files]);
    }
  };

  const removeFile = (index: number) => {
    setSelectedFiles(selectedFiles.filter((_, i) => i !== index));
  };

  const getCurrentLocation = () => {
    setIsGettingLocation(true);

    if (!navigator.geolocation) {
      toast({
        title: "Geolocation not supported",
        description: "Please enter your address manually.",
        variant: "destructive",
      });
      setIsGettingLocation(false);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (position) => {
        setFormData({
          ...formData,
          location: {
            lat: position.coords.latitude,
            lng: position.coords.longitude,
          },
        });
        setIsGettingLocation(false);
        toast({
          title: "Location detected",
          description: "Your current location has been set.",
        });
      },
      (error) => {
        console.error("Geolocation error:", error);
        toast({
          title: "Unable to get location",
          description: "Please enter your address manually or check location permissions.",
          variant: "destructive",
        });
        setIsGettingLocation(false);
      }
    );
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.categoryId || !formData.title || !formData.description) {
      toast({
        title: "Please fill in all required fields",
        variant: "destructive",
      });
      return;
    }

    try {
      // Upload files first if any
      let imageUrls: string[] = [];
      if (selectedFiles.length > 0) {
        const uploadResult = await uploadMutation.mutateAsync(selectedFiles);
        imageUrls = uploadResult.urls;
      }

      // Create the issue
      const issueData = {
        ...formData,
        imageUrls,
        location: formData.location.lat !== 0 ? formData.location : undefined,
      };

      await createIssueMutation.mutateAsync(issueData);
    } catch (error) {
      console.error("Submit error:", error);
    }
  };

  const categoryIcons: Record<string, string> = {
    pothole: "ðŸ›£ï¸",
    streetlight: "ðŸ’¡",
    garbage: "ðŸ—‘ï¸",
    water: "ðŸ’§",
    electricity: "âš¡",
    traffic: "ðŸš¦",
    parks: "ðŸŒ³",
    other: "ðŸ“",
  };

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="flex items-center space-x-3 mb-6">
        <Button variant="ghost" onClick={() => setLocation("/")} data-testid="button-back">
          <ArrowLeft className="h-4 w-4" />
        </Button>
        <h1 className="text-2xl font-bold">Report a Civic Issue</h1>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Report Form */}
        <div className="lg:col-span-2">
          <form onSubmit={handleSubmit} className="space-y-6">
            <Card className="border border-border">
              <CardHeader>
                <CardTitle>Issue Details</CardTitle>
              </CardHeader>
              <CardContent className="space-y-6">
                {/* Issue Category */}
                <div className="space-y-2">
                  <Label htmlFor="category">Issue Category *</Label>
                  <Select value={formData.categoryId} onValueChange={(value) => 
                    setFormData({ ...formData, categoryId: value })
                  }>
                    <SelectTrigger data-testid="select-category">
                      <SelectValue placeholder="Select a category" />
                    </SelectTrigger>
                    <SelectContent>
                      {categories.map((category: any) => (
                        <SelectItem key={category.id} value={category.id}>
                          <span className="flex items-center gap-2">
                            <span>{categoryIcons[category.code] || "ðŸ“"}</span>
                            {category.name}
                          </span>
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                {/* Issue Title */}
                <div className="space-y-2">
                  <Label htmlFor="title">Issue Title *</Label>
                  <Input
                    id="title"
                    placeholder="Brief description of the issue"
                    value={formData.title}
                    onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                    data-testid="input-title"
                  />
                </div>

                {/* Detailed Description */}
                <div className="space-y-2">
                  <Label htmlFor="description">Detailed Description *</Label>
                  <Textarea
                    id="description"
                    rows={4}
                    placeholder="Provide more details about the issue, when you noticed it, and how it affects you or others..."
                    value={formData.description}
                    onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                    data-testid="textarea-description"
                  />
                </div>

                {/* Location */}
                <div className="space-y-2">
                  <Label htmlFor="address">Location *</Label>
                  <div className="flex space-x-2">
                    <Input
                      id="address"
                      placeholder="Street address or landmark"
                      value={formData.address}
                      onChange={(e) => setFormData({ ...formData, address: e.target.value })}
                      className="flex-1"
                      data-testid="input-address"
                    />
                    <Button
                      type="button"
                      variant="secondary"
                      onClick={getCurrentLocation}
                      disabled={isGettingLocation}
                      data-testid="button-get-location"
                    >
                      <MapPin className="mr-2 h-4 w-4" />
                      {isGettingLocation ? "Getting..." : "Use GPS"}
                    </Button>
                  </div>
                  {formData.location.lat !== 0 && (
                    <p className="text-xs text-muted-foreground">
                      GPS: {formData.location.lat.toFixed(6)}, {formData.location.lng.toFixed(6)}
                    </p>
                  )}
                </div>

                {/* Photo Upload */}
                <div className="space-y-2">
                  <Label>Photos (Optional)</Label>
                  <div className="border-2 border-dashed border-border rounded-lg p-8 text-center hover:border-primary transition-colors">
                    <Camera className="mx-auto h-12 w-12 text-muted-foreground mb-4" />
                    <p className="text-muted-foreground mb-2">Click to upload photos or drag and drop</p>
                    <p className="text-xs text-muted-foreground">PNG, JPG up to 10MB each. Max 5 photos.</p>
                    <input
                      type="file"
                      multiple
                      accept="image/*"
                      onChange={handleFileChange}
                      className="hidden"
                      id="file-upload"
                      data-testid="input-file-upload"
                    />
                    <Button
                      type="button"
                      variant="outline"
                      className="mt-4"
                      onClick={() => document.getElementById('file-upload')?.click()}
                    >
                      Select Files
                    </Button>
                  </div>

                  {selectedFiles.length > 0 && (
                    <div className="space-y-2">
                      <p className="text-sm font-medium">Selected files:</p>
                      {selectedFiles.map((file, index) => (
                        <div key={index} className="flex items-center justify-between p-2 border border-border rounded">
                          <span className="text-sm">{file.name}</span>
                          <Button
                            type="button"
                            variant="ghost"
                            size="sm"
                            onClick={() => removeFile(index)}
                            data-testid={`button-remove-file-${index}`}
                          >
                            Remove
                          </Button>
                        </div>
                      ))}
                    </div>
                  )}
                </div>

                {/* Priority Level */}
                <div className="space-y-3">
                  <Label>Priority Level</Label>
                  <RadioGroup
                    value={formData.priority}
                    onValueChange={(value) => setFormData({ ...formData, priority: value })}
                  >
                    <div className="grid grid-cols-3 gap-3">
                      <Label className="flex items-center p-3 border border-border rounded-lg cursor-pointer hover:bg-accent">
                        <RadioGroupItem value="LOW" className="mr-3" />
                        <div>
                          <div className="font-medium text-green-600">Low</div>
                          <div className="text-xs text-muted-foreground">Non-urgent</div>
                        </div>
                      </Label>
                      <Label className="flex items-center p-3 border border-border rounded-lg cursor-pointer hover:bg-accent">
                        <RadioGroupItem value="MEDIUM" className="mr-3" />
                        <div>
                          <div className="font-medium text-orange-600">Medium</div>
                          <div className="text-xs text-muted-foreground">Standard</div>
                        </div>
                      </Label>
                      <Label className="flex items-center p-3 border border-border rounded-lg cursor-pointer hover:bg-accent">
                        <RadioGroupItem value="HIGH" className="mr-3" />
                        <div>
                          <div className="font-medium text-red-600">High</div>
                          <div className="text-xs text-muted-foreground">Urgent</div>
                        </div>
                      </Label>
                    </div>
                  </RadioGroup>
                </div>

                {/* Submit Button */}
                <div className="flex justify-end space-x-3 pt-6 border-t border-border">
                  <Button 
                    type="submit" 
                    className="btn-primary" 
                    disabled={createIssueMutation.isPending || uploadMutation.isPending}
                    data-testid="button-submit-issue"
                  >
                    {createIssueMutation.isPending || uploadMutation.isPending ? (
                      "Submitting..."
                    ) : (
                      <>
                        <Check className="mr-2 h-4 w-4" />
                        Submit Report
                      </>
                    )}
                  </Button>
                </div>
              </CardContent>
            </Card>
          </form>
        </div>

        {/* Sidebar Info */}
        <div className="space-y-6">
          {/* Tips Card */}
          <Card className="border border-border">
            <CardHeader>
              <CardTitle className="flex items-center">
                <Lightbulb className="mr-2 h-5 w-5 text-secondary" />
                Reporting Tips
              </CardTitle>
            </CardHeader>
            <CardContent>
              <ul className="space-y-3 text-sm">
                <li className="flex items-start">
                  <Check className="text-success mt-1 mr-2 h-3 w-3" />
                  <span>Include clear photos from multiple angles</span>
                </li>
                <li className="flex items-start">
                  <Check className="text-success mt-1 mr-2 h-3 w-3" />
                  <span>Provide exact location details</span>
                </li>
                <li className="flex items-start">
                  <Check className="text-success mt-1 mr-2 h-3 w-3" />
                  <span>Describe the impact on daily life</span>
                </li>
                <li className="flex items-start">
                  <Check className="text-success mt-1 mr-2 h-3 w-3" />
                  <span>Be specific about the problem</span>
                </li>
              </ul>
            </CardContent>
          </Card>

          {/* SLA Information */}
          <Card className="border border-border">
            <CardHeader>
              <CardTitle>Expected Resolution Times</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-3 text-sm">
                <div className="flex justify-between">
                  <span>Potholes</span>
                  <span className="text-muted-foreground">7-14 days</span>
                </div>
                <div className="flex justify-between">
                  <span>Street Lights</span>
                  <span className="text-muted-foreground">3-5 days</span>
                </div>
                <div className="flex justify-between">
                  <span>Garbage</span>
                  <span className="text-muted-foreground">1-2 days</span>
                </div>
                <div className="flex justify-between">
                  <span>Water Issues</span>
                  <span className="text-muted-foreground">2-4 days</span>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

client/src/pages/track-issues.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { apiClient } from "../lib/api";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { ArrowLeft, Search, Eye, MessageSquare, Star } from "lucide-react";
import { Issue } from "../types";
import IssueCard from "../components/issues/issue-card";
import { Skeleton } from "@/components/ui/skeleton";

export default function TrackIssues() {
  const [, setLocation] = useLocation();
  const [searchQuery, setSearchQuery] = useState("");
  const [statusFilter, setStatusFilter] = useState("");
  const [categoryFilter, setCategoryFilter] = useState("");
  const [page, setPage] = useState(1);

  const { data: issuesData, isLoading } = useQuery({
    queryKey: ['/api/issues/my', { page, status: statusFilter, search: searchQuery }],
    queryFn: () => apiClient.getMyIssues({ 
      page, 
      limit: 10, 
      status: statusFilter || undefined 
    }),
  });

  const { data: categories = [] } = useQuery({
    queryKey: ['/api/catalog/categories'],
    queryFn: () => apiClient.getCategories(),
  });

  const handleSearch = () => {
    setPage(1);
    // Query will automatically refetch due to searchQuery dependency
  };

  const issues = issuesData?.issues || [];
  const total = issuesData?.total || 0;
  const totalPages = Math.ceil(total / 10);

  if (isLoading) {
    return (
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="space-y-6">
          <div className="flex items-center space-x-3">
            <Skeleton className="h-10 w-10" />
            <Skeleton className="h-8 w-48" />
          </div>
          <div className="space-y-4">
            {[1, 2, 3].map((i) => (
              <Skeleton key={i} className="h-48 w-full" />
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center space-x-3">
          <Button variant="ghost" onClick={() => setLocation("/")} data-testid="button-back">
            <ArrowLeft className="h-4 w-4" />
          </Button>
          <h1 className="text-2xl font-bold">Track Your Issues</h1>
        </div>
        <div className="flex items-center space-x-3">
          <Input
            type="text"
            placeholder="Search by ticket number..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="w-64"
            data-testid="input-search"
          />
          <Button onClick={handleSearch} data-testid="button-search">
            <Search className="h-4 w-4" />
          </Button>
        </div>
      </div>

      {/* Filters */}
      <Card className="mb-6">
        <CardContent className="p-4">
          <div className="flex flex-wrap gap-3">
            <Select value={statusFilter} onValueChange={setStatusFilter}>
              <SelectTrigger className="w-48" data-testid="select-status-filter">
                <SelectValue placeholder="All Statuses" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="">All Statuses</SelectItem>
                <SelectItem value="SUBMITTED">Submitted</SelectItem>
                <SelectItem value="TRIAGED">Triaged</SelectItem>
                <SelectItem value="ASSIGNED">Assigned</SelectItem>
                <SelectItem value="IN_PROGRESS">In Progress</SelectItem>
                <SelectItem value="RESOLVED">Resolved</SelectItem>
                <SelectItem value="REJECTED">Rejected</SelectItem>
              </SelectContent>
            </Select>

            <Select value={categoryFilter} onValueChange={setCategoryFilter}>
              <SelectTrigger className="w-48" data-testid="select-category-filter">
                <SelectValue placeholder="All Categories" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="">All Categories</SelectItem>
                {categories.map((category: any) => (
                  <SelectItem key={category.id} value={category.id}>
                    {category.name}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* Issues List */}
      <div className="space-y-4">
        {issues.length === 0 ? (
          <Card className="border border-border">
            <CardContent className="p-12 text-center">
              <div className="text-muted-foreground">
                <Search className="h-12 w-12 mx-auto mb-4 opacity-50" />
                <h3 className="text-lg font-medium mb-2">No issues found</h3>
                <p>You haven't reported any issues yet, or none match your current filters.</p>
              </div>
            </CardContent>
          </Card>
        ) : (
          issues.map((issue: Issue) => (
            <IssueCard key={issue.id} issue={issue} showActions />
          ))
        )}
      </div>

      {/* Pagination */}
      {totalPages > 1 && (
        <div className="flex justify-center mt-8">
          <nav className="flex space-x-2">
            <Button 
              variant="outline" 
              onClick={() => setPage(page - 1)} 
              disabled={page === 1}
              data-testid="button-previous-page"
            >
              Previous
            </Button>

            {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
              const pageNum = i + 1;
              return (
                <Button
                  key={pageNum}
                  variant={page === pageNum ? "default" : "outline"}
                  onClick={() => setPage(pageNum)}
                  data-testid={`button-page-${pageNum}`}
                >
                  {pageNum}
                </Button>
              );
            })}

            <Button 
              variant="outline" 
              onClick={() => setPage(page + 1)} 
              disabled={page === totalPages}
              data-testid="button-next-page"
            >
              Next
            </Button>
          </nav>
        </div>
      )}
    </div>
  );
}

client/src/types/index.ts.content:
export type UserRole = 'CITIZEN' | 'OFFICER' | 'SUPERVISOR' | 'ADMIN';

export type IssueStatus = 
  | 'DRAFT' 
  | 'SUBMITTED' 
  | 'TRIAGED' 
  | 'ASSIGNED' 
  | 'IN_PROGRESS' 
  | 'PENDING_USER_INFO' 
  | 'RESOLVED' 
  | 'REJECTED';

export type IssuePriority = 'LOW' | 'MEDIUM' | 'HIGH' | 'CRITICAL';

export interface User {
  id: string;
  email: string;
  name: string;
  phone?: string;
  role: UserRole;
  wardId?: string;
  isActive: boolean;
  createdAt: string;
  updatedAt: string;
}

export interface Category {
  id: string;
  name: string;
  code: string;
  icon?: string;
  slaHours: number;
  priorityWeight: number;
  departmentId: string;
}

export interface Ward {
  id: string;
  name: string;
  code: string;
  geojson?: any;
}

export interface Department {
  id: string;
  name: string;
  code: string;
  email?: string;
  phone?: string;
}

export interface Issue {
  id: string;
  ticketNo: string;
  title: string;
  description: string;
  categoryId: string;
  wardId?: string;
  departmentId?: string;
  reporterId: string;
  assigneeId?: string;
  status: IssueStatus;
  priority?: IssuePriority;
  priorityScore?: number;
  location?: {
    lat: number;
    lng: number;
  };
  address?: string;
  imageUrls: string[];
  videoUrls: string[];
  duplicateOfIssueId?: string;
  rejectedReason?: string;
  resolvedAt?: string;
  createdAt: string;
  updatedAt: string;
  category?: Category;
  ward?: Ward;
  department?: Department;
  reporter?: User;
  assignee?: User;
  comments?: Comment[];
}

export interface Comment {
  id: string;
  issueId: string;
  authorId: string;
  body: string;
  attachments: string[];
  createdAt: string;
  author?: User;
}

export interface ApiResponse<T = any> {
  data?: T;
  message?: string;
  error?: string;
}

export interface PaginatedResponse<T> {
  items: T;
  total: number;
  page: number;
  limit: number;
  totalPages: number;
}

export interface IssueStats {
  total: number;
  byStatus: Record<string, number>;
  byPriority: Record<string, number>;
  byCategory: Record<string, number>;
  byWard: Record<string, number>;
}

client/src/components/admin/issues-table.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiClient } from "../../lib/api";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { Badge } from "@/components/ui/badge";
import { Search, Edit, Eye, UserPlus } from "lucide-react";
import StatusBadge from "../issues/status-badge";
import { Issue } from "../../types";
import { format } from "date-fns";
import { useToast } from "../../hooks/use-toast";

export default function IssuesTable() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [filters, setFilters] = useState({
    status: "",
    categoryId: "",
    priority: "",
    search: "",
    page: 1,
    limit: 10,
  });

  const [selectedIssues, setSelectedIssues] = useState<string[]>([]);

  const { data: issuesData, isLoading } = useQuery({
    queryKey: ['/api/admin/issues', filters],
    queryFn: () => apiClient.getAdminIssues(filters),
  });

  const { data: categories = [] } = useQuery({
    queryKey: ['/api/catalog/categories'],
    queryFn: () => apiClient.getCategories(),
  });

  const { data: users = [] } = useQuery({
    queryKey: ['/api/admin/users'],
    queryFn: () => apiClient.getUsers(),
  });

  const updateStatusMutation = useMutation({
    mutationFn: ({ issueId, status, rejectedReason }: { 
      issueId: string; 
      status: string; 
      rejectedReason?: string; 
    }) => apiClient.updateIssueStatus(issueId, status, rejectedReason),
    onSuccess: () => {
      toast({
        title: "Status updated successfully",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/issues'] });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to update status",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const assignIssueMutation = useMutation({
    mutationFn: ({ issueId, assigneeId }: { issueId: string; assigneeId: string }) =>
      apiClient.assignIssue(issueId, assigneeId),
    onSuccess: () => {
      toast({
        title: "Issue assigned successfully",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/issues'] });
    },
    onError: (error: any) => {
      toast({
        title: "Failed to assign issue",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleSearch = () => {
    setFilters({ ...filters, page: 1 });
  };

  const handleSelectIssue = (issueId: string, checked: boolean) => {
    if (checked) {
      setSelectedIssues([...selectedIssues, issueId]);
    } else {
      setSelectedIssues(selectedIssues.filter(id => id !== issueId));
    }
  };

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      setSelectedIssues(issues.map((issue: Issue) => issue.id));
    } else {
      setSelectedIssues([]);
    }
  };

  const issues = issuesData?.issues || [];
  const total = issuesData?.total || 0;
  const totalPages = Math.ceil(total / filters.limit);

  const getPriorityColor = (priority?: string) => {
    switch (priority) {
      case 'HIGH': return 'border-red-500 text-red-700';
      case 'MEDIUM': return 'border-yellow-500 text-yellow-700';
      case 'LOW': return 'border-green-500 text-green-700';
      case 'CRITICAL': return 'border-red-700 text-red-800';
      default: return 'border-gray-500 text-gray-700';
    }
  };

  return (
    <Card className="border border-border">
      {/* Filters Bar */}
      <CardContent className="p-4 border-b border-border">
        <div className="flex flex-wrap gap-3">
          <Select value={filters.status} onValueChange={(value) => setFilters({ ...filters, status: value })}>
            <SelectTrigger className="w-48" data-testid="select-status-filter">
              <SelectValue placeholder="All Statuses" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="">All Statuses</SelectItem>
              <SelectItem value="SUBMITTED">Submitted</SelectItem>
              <SelectItem value="TRIAGED">Triaged</SelectItem>
              <SelectItem value="ASSIGNED">Assigned</SelectItem>
              <SelectItem value="IN_PROGRESS">In Progress</SelectItem>
              <SelectItem value="RESOLVED">Resolved</SelectItem>
              <SelectItem value="REJECTED">Rejected</SelectItem>
            </SelectContent>
          </Select>

          <Select value={filters.categoryId} onValueChange={(value) => setFilters({ ...filters, categoryId: value })}>
            <SelectTrigger className="w-48" data-testid="select-category-filter">
              <SelectValue placeholder="All Categories" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="">All Categories</SelectItem>
              {categories.map((category: any) => (
                <SelectItem key={category.id} value={category.id}>
                  {category.name}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>

          <Select value={filters.priority} onValueChange={(value) => setFilters({ ...filters, priority: value })}>
            <SelectTrigger className="w-48" data-testid="select-priority-filter">
              <SelectValue placeholder="All Priorities" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="">All Priorities</SelectItem>
              <SelectItem value="HIGH">High</SelectItem>
              <SelectItem value="MEDIUM">Medium</SelectItem>
              <SelectItem value="LOW">Low</SelectItem>
              <SelectItem value="CRITICAL">Critical</SelectItem>
            </SelectContent>
          </Select>

          <Input
            placeholder="Search issues..."
            value={filters.search}
            onChange={(e) => setFilters({ ...filters, search: e.target.value })}
            className="w-64"
            data-testid="input-search"
          />

          <Button onClick={handleSearch} data-testid="button-search">
            <Search className="mr-2 h-4 w-4" />
            Search
          </Button>
        </div>
      </CardContent>

      {/* Issues Table */}
      <div className="overflow-x-auto">
        <table className="w-full">
          <thead className="bg-muted">
            <tr>
              <th className="px-4 py-3 text-left text-xs font-medium text-muted-foreground uppercase tracking-wider">
                <Checkbox
                  checked={selectedIssues.length === issues.length && issues.length > 0}
                  onCheckedChange={handleSelectAll}
                  data-testid="checkbox-select-all"
                />
              </th>
              <th className="px-4 py-3 text-left text-xs font-medium text-muted-foreground uppercase tracking-wider">
                Ticket
              </th>
              <th className="px-4 py-3 text-left text-xs font-medium text-muted-foreground uppercase tracking-wider">
                Issue
              </th>
              <th className="px-4 py-3 text-left text-xs font-medium text-muted-foreground uppercase tracking-wider">
                Category
              </th>
              <th className="px-4 py-3 text-left text-xs font-medium text-muted-foreground uppercase tracking-wider">
                Status
              </th>
              <th className="px-4 py-3 text-left text-xs font-medium text-muted-foreground uppercase tracking-wider">
                Priority
              </th>
              <th className="px-4 py-3 text-left text-xs font-medium text-muted-foreground uppercase tracking-wider">
                Assigned To
              </th>
              <th className="px-4 py-3 text-left text-xs font-medium text-muted-foreground uppercase tracking-wider">
                Created
              </th>
              <th className="px-4 py-3 text-left text-xs font-medium text-muted-foreground uppercase tracking-wider">
                Actions
              </th>
            </tr>
          </thead>
          <tbody className="divide-y divide-border">
            {issues.map((issue: Issue) => (
              <tr key={issue.id} className="hover:bg-accent/50 transition-colors" data-testid={`row-issue-${issue.ticketNo}`}>
                <td className="px-4 py-4">
                  <Checkbox
                    checked={selectedIssues.includes(issue.id)}
                    onCheckedChange={(checked) => handleSelectIssue(issue.id, checked as boolean)}
                    data-testid={`checkbox-issue-${issue.id}`}
                  />
                </td>
                <td className="px-4 py-4 text-sm font-mono" data-testid={`text-ticket-${issue.id}`}>
                  {issue.ticketNo}
                </td>
                <td className="px-4 py-4">
                  <div className="flex items-center space-x-3">
                    {issue.imageUrls && issue.imageUrls.length > 0 && (
                      <img 
                        src={issue.imageUrls[0]} 
                        alt="Issue thumbnail" 
                        className="w-8 h-8 rounded object-cover"
                      />
                    )}
                    <div>
                      <p className="text-sm font-medium" data-testid={`text-title-${issue.id}`}>
                        {issue.title}
                      </p>
                      <p className="text-xs text-muted-foreground">
                        {issue.address}
                      </p>
                    </div>
                  </div>
                </td>
                <td className="px-4 py-4 text-sm" data-testid={`text-category-${issue.id}`}>
                  {issue.category?.name}
                </td>
                <td className="px-4 py-4">
                  <StatusBadge status={issue.status} />
                </td>
                <td className="px-4 py-4">
                  {issue.priority && (
                    <Badge variant="outline" className={`text-xs ${getPriorityColor(issue.priority)}`}>
                      {issue.priority}
                    </Badge>
                  )}
                </td>
                <td className="px-4 py-4 text-sm" data-testid={`text-assignee-${issue.id}`}>
                  {issue.assignee?.name || 'Unassigned'}
                </td>
                <td className="px-4 py-4 text-sm text-muted-foreground">
                  {format(new Date(issue.createdAt), 'MMM d, yyyy')}
                </td>
                <td className="px-4 py-4">
                  <div className="flex space-x-2">
                    <Button variant="ghost" size="sm" data-testid={`button-edit-${issue.id}`}>
                      <Edit className="h-4 w-4" />
                    </Button>
                    <Button variant="ghost" size="sm" data-testid={`button-view-${issue.id}`}>
                      <Eye className="h-4 w-4" />
                    </Button>
                    <Button variant="ghost" size="sm" data-testid={`button-assign-${issue.id}`}>
                      <UserPlus className="h-4 w-4" />
                    </Button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Bulk Actions & Pagination */}
      <CardContent className="p-4 border-t border-border flex justify-between items-center">
        <div className="flex items-center space-x-3">
          <span className="text-sm text-muted-foreground" data-testid="text-selected-count">
            {selectedIssues.length} items selected
          </span>
          <Button variant="outline" size="sm" disabled={selectedIssues.length === 0} data-testid="button-bulk-assign">
            Bulk Assign
          </Button>
          <Button variant="outline" size="sm" disabled={selectedIssues.length === 0} data-testid="button-bulk-status">
            Bulk Status
          </Button>
          <Button variant="outline" size="sm" disabled={selectedIssues.length === 0} data-testid="button-export-selected">
            Export Selected
          </Button>
        </div>

        {totalPages > 1 && (
          <nav className="flex space-x-2">
            <Button 
              variant="outline" 
              size="sm"
              onClick={() => setFilters({ ...filters, page: filters.page - 1 })} 
              disabled={filters.page === 1}
              data-testid="button-previous-page"
            >
              Previous
            </Button>

            {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
              const pageNum = i + 1;
              return (
                <Button
                  key={pageNum}
                  variant={filters.page === pageNum ? "default" : "outline"}
                  size="sm"
                  onClick={() => setFilters({ ...filters, page: pageNum })}
                  data-testid={`button-page-${pageNum}`}
                >
                  {pageNum}
                </Button>
              );
            })}

            <Button 
              variant="outline" 
              size="sm"
              onClick={() => setFilters({ ...filters, page: filters.page + 1 })} 
              disabled={filters.page === totalPages}
              data-testid="button-next-page"
            >
              Next
            </Button>
          </nav>
        )}
      </CardContent>
    </Card>
  );
}

