client/src/components/issues/issue-card..content:
import { Issue } from "../../types";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { MessageSquare, Eye, Star, MapPin, Calendar } from "lucide-react";
import StatusBadge from "./status-badge";
import { format } from "date-fns";

interface IssueCardProps {
  issue: Issue;
  showActions?: boolean;
  onViewDetails?: (issue: Issue) => void;
  onAddComment?: (issue: Issue) => void;
  onRate?: (issue: Issue) => void;
}

export default function IssueCard({ 
  issue, 
  showActions = false, 
  onViewDetails, 
  onAddComment, 
  onRate 
}: IssueCardProps) {
  const getPriorityClass = (priority?: string) => {
    switch (priority) {
      case 'HIGH': return 'priority-high';
      case 'MEDIUM': return 'priority-medium';
      case 'LOW': return 'priority-low';
      case 'CRITICAL': return 'priority-critical';
      default: return '';
    }
  };

  const getStatusSteps = () => {
    const steps = [
      { key: 'SUBMITTED', label: 'Submitted', completed: false },
      { key: 'TRIAGED', label: 'Triaged', completed: false },
      { key: 'ASSIGNED', label: 'Assigned', completed: false },
      { key: 'IN_PROGRESS', label: 'In Progress', completed: false },
      { key: 'RESOLVED', label: 'Resolved', completed: false },
    ];

    const statusOrder = ['SUBMITTED', 'TRIAGED', 'ASSIGNED', 'IN_PROGRESS', 'RESOLVED'];
    const currentIndex = statusOrder.indexOf(issue.status);

    steps.forEach((step, index) => {
      if (index <= currentIndex) {
        step.completed = true;
      }
      if (index === currentIndex) {
        step.label = step.label; // Current step
      }
    });

    return steps;
  };

  const statusSteps = getStatusSteps();

  return (
    <Card className={`border border-border ${getPriorityClass(issue.priority)}`} data-testid={`card-issue-${issue.ticketNo}`}>
      <CardContent className="p-6">
        <div className="flex justify-between items-start mb-4">
          <div className="flex-1">
            <div className="flex items-center space-x-3 mb-2">
              <h3 className="font-semibold text-lg" data-testid={`text-issue-title-${issue.id}`}>
                {issue.title}
              </h3>
              <StatusBadge status={issue.status} />
              {issue.priority && (
                <Badge variant="outline" className={`text-xs ${
                  issue.priority === 'HIGH' ? 'border-red-500 text-red-700' :
                  issue.priority === 'MEDIUM' ? 'border-yellow-500 text-yellow-700' :
                  issue.priority === 'LOW' ? 'border-green-500 text-green-700' :
                  'border-red-700 text-red-800'
                }`}>
                  {issue.priority}
                </Badge>
              )}
            </div>
            <p className="text-sm text-muted-foreground mb-2" data-testid={`text-issue-ticket-${issue.id}`}>
              Ticket #{issue.ticketNo} â€¢ {issue.category?.name}
            </p>
            <div className="flex items-center space-x-4 text-sm text-muted-foreground">
              <div className="flex items-center space-x-1">
                <MapPin className="h-3 w-3" />
                <span>{issue.address || 'Location not specified'}</span>
              </div>
              {issue.ward && (
                <span>Ward: {issue.ward.name}</span>
              )}
            </div>
          </div>
          <div className="text-right text-sm text-muted-foreground">
            <div className="flex items-center space-x-1 mb-1">
              <Calendar className="h-3 w-3" />
              <span>Reported: {format(new Date(issue.createdAt), 'MMM d, yyyy')}</span>
            </div>
            <p>Updated: {format(new Date(issue.updatedAt), 'MMM d, yyyy')}</p>
          </div>
        </div>

        {/* Progress Timeline */}
        <div className="mb-4">
          <div className="flex items-center space-x-4 text-sm">
            {statusSteps.map((step, index) => (
              <div key={step.key} className="flex items-center space-x-2">
                <div className={`w-3 h-3 rounded-full ${
                  step.completed ? 'bg-success' : 
                  step.key === issue.status ? 'bg-warning animate-pulse' : 
                  'bg-muted'
                }`}></div>
                <span className={
                  step.completed ? 'text-success' : 
                  step.key === issue.status ? 'text-warning font-medium' : 
                  'text-muted-foreground'
                }>
                  {step.label}
                </span>
                {index < statusSteps.length - 1 && (
                  <div className="w-4 h-px bg-border"></div>
                )}
              </div>
            ))}
          </div>
        </div>

        {/* Latest Update */}
        {issue.status === 'IN_PROGRESS' && (
          <div className="bg-accent p-3 rounded-lg mb-4">
            <p className="text-sm font-medium">Latest Update:</p>
            <p className="text-sm text-muted-foreground">
              Work is currently in progress. We'll keep you updated on the status.
            </p>
          </div>
        )}

        {issue.status === 'RESOLVED' && issue.resolvedAt && (
          <div className="bg-green-50 dark:bg-green-900/20 border border-green-200 dark:border-green-800 p-3 rounded-lg mb-4">
            <p className="text-sm font-medium text-green-800 dark:text-green-400">Issue Resolved</p>
            <p className="text-sm text-green-700 dark:text-green-300">
              Resolved on {format(new Date(issue.resolvedAt), 'MMM d, yyyy')}. Thank you for reporting this issue!
            </p>
          </div>
        )}

        {issue.status === 'REJECTED' && issue.rejectedReason && (
          <div className="bg-red-50 dark:bg-red-900/20 border border-red-200 dark:border-red-800 p-3 rounded-lg mb-4">
            <p className="text-sm font-medium text-red-800 dark:text-red-400">Issue Rejected</p>
            <p className="text-sm text-red-700 dark:text-red-300">{issue.rejectedReason}</p>
          </div>
        )}

        {/* Action Buttons */}
        {showActions && (
          <div className="flex justify-between items-center">
            <div className="flex space-x-2">
              {issue.imageUrls && issue.imageUrls.length > 0 && (
                <div className="flex items-center space-x-2">
                  <img 
                    src={issue.imageUrls[0]} 
                    alt="Issue thumbnail" 
                    className="w-8 h-8 rounded object-cover"
                    data-testid={`img-thumbnail-${issue.id}`}
                  />
                  {issue.imageUrls.length > 1 && (
                    <span className="text-xs text-muted-foreground">
                      +{issue.imageUrls.length - 1} more
                    </span>
                  )}
                </div>
              )}
            </div>
            <div className="flex space-x-2">
              <Button 
                variant="outline" 
                size="sm" 
                onClick={() => onAddComment?.(issue)}
                data-testid={`button-comment-${issue.id}`}
              >
                <MessageSquare className="mr-1 h-3 w-3" />
                Comment
              </Button>
              <Button 
                variant="outline" 
                size="sm" 
                onClick={() => onViewDetails?.(issue)}
                data-testid={`button-view-details-${issue.id}`}
              >
                <Eye className="mr-1 h-3 w-3" />
                View Details
              </Button>
              {issue.status === 'RESOLVED' && (
                <Button 
                  variant="outline" 
                  size="sm" 
                  onClick={() => onRate?.(issue)}
                  data-testid={`button-rate-${issue.id}`}
                >
                  <Star className="mr-1 h-3 w-3" />
                  Rate
                </Button>
              )}
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/issues/issue-form..content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { apiClient } from "../../lib/api";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Camera, MapPin } from "lucide-react";
import { useToast } from "../../hooks/use-toast";

interface IssueFormProps {
  onSubmit: (data: any) => void;
  isSubmitting?: boolean;
}

export default function IssueForm({ onSubmit, isSubmitting = false }: IssueFormProps) {
  const { toast } = useToast();

  const [formData, setFormData] = useState({
    categoryId: "",
    title: "",
    description: "",
    address: "",
    location: { lat: 0, lng: 0 },
    priority: "MEDIUM",
  });

  const [selectedFiles, setSelectedFiles] = useState<File[]>([]);
  const [isGettingLocation, setIsGettingLocation] = useState(false);

  const { data: categories = [] } = useQuery({
    queryKey: ['/api/catalog/categories'],
    queryFn: () => apiClient.getCategories(),
  });

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files) {
      const files = Array.from(e.target.files);
      if (files.length + selectedFiles.length > 5) {
        toast({
          title: "Too many files",
          description: "You can only upload up to 5 photos.",
          variant: "destructive",
        });
        return;
      }
      setSelectedFiles([...selectedFiles, ...files]);
    }
  };

  const removeFile = (index: number) => {
    setSelectedFiles(selectedFiles.filter((_, i) => i !== index));
  };

  const getCurrentLocation = () => {
    setIsGettingLocation(true);

    if (!navigator.geolocation) {
      toast({
        title: "Geolocation not supported",
        description: "Please enter your address manually.",
        variant: "destructive",
      });
      setIsGettingLocation(false);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (position) => {
        setFormData({
          ...formData,
          location: {
            lat: position.coords.latitude,
            lng: position.coords.longitude,
          },
        });
        setIsGettingLocation(false);
        toast({
          title: "Location detected",
          description: "Your current location has been set.",
        });
      },
      (error) => {
        console.error("Geolocation error:", error);
        toast({
          title: "Unable to get location",
          description: "Please enter your address manually or check location permissions.",
          variant: "destructive",
        });
        setIsGettingLocation(false);
      }
    );
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.categoryId || !formData.title || !formData.description) {
      toast({
        title: "Please fill in all required fields",
        variant: "destructive",
      });
      return;
    }

    onSubmit({
      ...formData,
      files: selectedFiles,
      location: formData.location.lat !== 0 ? formData.location : undefined,
    });
  };

  const categoryIcons: Record<string, string> = {
    pothole: "ðŸ›£ï¸",
    streetlight: "ðŸ’¡",
    garbage: "ðŸ—‘ï¸",
    water: "ðŸ’§",
    electricity: "âš¡",
    traffic: "ðŸš¦",
    parks: "ðŸŒ³",
    other: "ðŸ“",
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      {/* Issue Category */}
      <div className="space-y-2">
        <Label htmlFor="category">Issue Category *</Label>
        <Select value={formData.categoryId} onValueChange={(value) => 
          setFormData({ ...formData, categoryId: value })
        }>
          <SelectTrigger data-testid="select-category">
            <SelectValue placeholder="Select a category" />
          </SelectTrigger>
          <SelectContent>
            {categories.map((category: any) => (
              <SelectItem key={category.id} value={category.id}>
                <span className="flex items-center gap-2">
                  <span>{categoryIcons[category.code] || "ðŸ“"}</span>
                  {category.name}
                </span>
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
      </div>

      {/* Issue Title */}
      <div className="space-y-2">
        <Label htmlFor="title">Issue Title *</Label>
        <Input
          id="title"
          placeholder="Brief description of the issue"
          value={formData.title}
          onChange={(e) => setFormData({ ...formData, title: e.target.value })}
          data-testid="input-title"
        />
      </div>

      {/* Detailed Description */}
      <div className="space-y-2">
        <Label htmlFor="description">Detailed Description *</Label>
        <Textarea
          id="description"
          rows={4}
          placeholder="Provide more details about the issue, when you noticed it, and how it affects you or others..."
          value={formData.description}
          onChange={(e) => setFormData({ ...formData, description: e.target.value })}
          data-testid="textarea-description"
        />
      </div>

      {/* Location */}
      <div className="space-y-2">
        <Label htmlFor="address">Location *</Label>
        <div className="flex space-x-2">
          <Input
            id="address"
            placeholder="Street address or landmark"
            value={formData.address}
            onChange={(e) => setFormData({ ...formData, address: e.target.value })}
            className="flex-1"
            data-testid="input-address"
          />
          <Button
            type="button"
            variant="secondary"
            onClick={getCurrentLocation}
            disabled={isGettingLocation}
            data-testid="button-get-location"
          >
            <MapPin className="mr-2 h-4 w-4" />
            {isGettingLocation ? "Getting..." : "Use GPS"}
          </Button>
        </div>
        {formData.location.lat !== 0 && (
          <p className="text-xs text-muted-foreground">
            GPS: {formData.location.lat.toFixed(6)}, {formData.location.lng.toFixed(6)}
          </p>
        )}
      </div>

      {/* Photo Upload */}
      <div className="space-y-2">
        <Label>Photos (Optional)</Label>
        <div className="border-2 border-dashed border-border rounded-lg p-8 text-center hover:border-primary transition-colors">
          <Camera className="mx-auto h-12 w-12 text-muted-foreground mb-4" />
          <p className="text-muted-foreground mb-2">Click to upload photos or drag and drop</p>
          <p className="text-xs text-muted-foreground">PNG, JPG up to 10MB each. Max 5 photos.</p>
          <input
            type="file"
            multiple
            accept="image/*"
            onChange={handleFileChange}
            className="hidden"
            id="file-upload"
            data-testid="input-file-upload"
          />
          <Button
            type="button"
            variant="outline"
            className="mt-4"
            onClick={() => document.getElementById('file-upload')?.click()}
          >
            Select Files
          </Button>
        </div>

        {selectedFiles.length > 0 && (
          <div className="space-y-2">
            <p className="text-sm font-medium">Selected files:</p>
            {selectedFiles.map((file, index) => (
              <div key={index} className="flex items-center justify-between p-2 border border-border rounded">
                <span className="text-sm">{file.name}</span>
                <Button
                  type="button"
                  variant="ghost"
                  size="sm"
                  onClick={() => removeFile(index)}
                  data-testid={`button-remove-file-${index}`}
                >
                  Remove
                </Button>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Priority Level */}
      <div className="space-y-3">
        <Label>Priority Level</Label>
        <RadioGroup
          value={formData.priority}
          onValueChange={(value) => setFormData({ ...formData, priority: value })}
        >
          <div className="grid grid-cols-3 gap-3">
            <Label className="flex items-center p-3 border border-border rounded-lg cursor-pointer hover:bg-accent">
              <RadioGroupItem value="LOW" className="mr-3" />
              <div>
                <div className="font-medium text-green-600">Low</div>
                <div className="text-xs text-muted-foreground">Non-urgent</div>
              </div>
            </Label>
            <Label className="flex items-center p-3 border border-border rounded-lg cursor-pointer hover:bg-accent">
              <RadioGroupItem value="MEDIUM" className="mr-3" />
              <div>
                <div className="font-medium text-orange-600">Medium</div>
                <div className="text-xs text-muted-foreground">Standard</div>
              </div>
            </Label>
            <Label className="flex items-center p-3 border border-border rounded-lg cursor-pointer hover:bg-accent">
              <RadioGroupItem value="HIGH" className="mr-3" />
              <div>
                <div className="font-medium text-red-600">High</div>
                <div className="text-xs text-muted-foreground">Urgent</div>
              </div>
            </Label>
          </div>
        </RadioGroup>
      </div>

      <Button type="submit" className="w-full btn-primary" disabled={isSubmitting} data-testid="button-submit">
        {isSubmitting ? "Submitting..." : "Submit Report"}
      </Button>
    </form>
  );
}

client/src/components/issues/status-badg.content:
import { Badge } from "@/components/ui/badge";
import { cn } from "@/lib/utils";
import { IssueStatus } from "../../types";

interface StatusBadgeProps {
  status: IssueStatus;
  className?: string;
}

export default function StatusBadge({ status, className }: StatusBadgeProps) {
  const getStatusConfig = (status: IssueStatus) => {
    switch (status) {
      case 'DRAFT':
        return {
          label: 'Draft',
          className: 'status-draft bg-gray-100 text-gray-800 dark:bg-gray-900/20 dark:text-gray-400',
        };
      case 'SUBMITTED':
        return {
          label: 'Submitted',
          className: 'status-submitted',
        };
      case 'TRIAGED':
        return {
          label: 'Triaged',
          className: 'status-triaged',
        };
      case 'ASSIGNED':
        return {
          label: 'Assigned',
          className: 'status-assigned',
        };
      case 'IN_PROGRESS':
        return {
          label: 'In Progress',
          className: 'status-in-progress',
        };
      case 'PENDING_USER_INFO':
        return {
          label: 'Pending Info',
          className: 'status-pending-user-info',
        };
      case 'RESOLVED':
        return {
          label: 'Resolved',
          className: 'status-resolved',
        };
      case 'REJECTED':
        return {
          label: 'Rejected',
          className: 'status-rejected',
        };
      default:
        return {
          label: status,
          className: 'bg-gray-100 text-gray-800 dark:bg-gray-900/20 dark:text-gray-400',
        };
    }
  };

  const config = getStatusConfig(status);

  return (
    <Badge 
      className={cn("px-2 py-1 text-xs font-medium", config.className, className)}
      data-testid={`badge-status-${status.toLowerCase()}`}
    >
      {config.label}
    </Badge>
  );
}

